# 编写任务代码的pyfrom celery_tasks.sms.yuntongxun.sms import CCP# from celery_tasks.main import celery_appfrom celery_tasks.main import app@app.task(name='send_sms_code')  # 只有用此装饰器装饰过的函数才能称为celery任务def send_sms_code(mobile, sms_code):    """    发短信的导步任务    :param mobile: 要收短信的手机号    :param sms_code: 短信验证码    """    # 利用容联云平台进行发送短信    CCP().send_template_sms(mobile, [sms_code, 5], 1)